cv::Mat get_image(const std::string& namefile)
{
  cv::FileStorage fs(namefile, cv::FileStorage::READ);
  cv::Mat features;
  fs["deep_features"] >> features;
  return features;
}


int main(int argc, char* argvc[])
{
	psc_utils::Kohonen map("kohonen_map.bv", 0);
	for(int p = 57; p < N ; p+=5)
	{
      psc_utils::HeatMap hmap(TAUX);
      for(int q = 0; q < 5; ++q)
      {
        std::string namefile = dirname + namefiles[p+q];
        cv::Mat mat = get_image(namefile);
        psc_utils::ImageFeatures image(mat, p);
        for (int i = 0; i < FEATURES_SIZE_X; i++)
		    {
			       for (int j = 0; j < FEATURES_SIZE_Y; j++)
			       {
				          psc_utils::Vecteur v = image.getVector(i, j);
                  hmap.heat(v, map);
			       }
		    }
      }
    hmap.print();
    hmap.save("heatmaps/heat_map_" + std::to_string(p) + ".hbv");
    float f = ((float)p / (float)N) * 100.0;
    std::cout << f << " % environ" << std::endl;

  }


	return 0;
}
